export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { setupAuth, isAuthenticated, hashPassword, comparePassword, loginUser, logoutUser } from "./auth";
import { insertPredictionSchema, insertVoteSchema, loginSchema, registerSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Initialize default categories
  const defaultCategories = [
    { name: "Tech", emoji: "ðŸš€", color: "bg-blue-100 text-blue-800" },
    { name: "Sports", emoji: "âš½", color: "bg-green-100 text-green-800" },
    { name: "Crypto", emoji: "ðŸ’°", color: "bg-yellow-100 text-yellow-800" },
    { name: "Memes", emoji: "ðŸ˜‚", color: "bg-pink-100 text-pink-800" },
    { name: "Food", emoji: "ðŸ•", color: "bg-orange-100 text-orange-800" },
  ];

  // Ensure default categories exist
  for (const category of defaultCategories) {
    try {
      await storage.createCategory(category);
    } catch (error) {
      // Category might already exist, ignore error
    }
  }

  // Auth routes
  app.post('/api/auth/register', async (req, res) => {
    try {
      const validatedData = registerSchema.parse(req.body);

      // Check if username already exists
      const existingUser = await storage.getUserByUsername(validatedData.username);
      if (existingUser) {
        return res.status(400).json({ message: "Username already exists" });
      }

      // Hash password
      const hashedPassword = await hashPassword(validatedData.password);

      // Create user
      const user = await storage.createUser({
        ...validatedData,
        password: hashedPassword,
      });

      // Login user
      await loginUser(req, user);

      // Return user without password
      const { password, ...userWithoutPassword } = user;
      res.status(201).json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid registration data", errors: error.errors });
      }
      console.error("Error registering user:", error);
      res.status(500).json({ message: "Failed to register user" });
    }
  });

  app.post('/api/auth/login', async (req, res) => {
    try {
      const validatedData = loginSchema.parse(req.body);

      // Find user by username
      const user = await storage.getUserByUsername(validatedData.username);
      if (!user) {
        return res.status(401).json({ message: "Invalid username or password" });
      }

      // Verify password
      const isValid = await comparePassword(validatedData.password, user.password);
      if (!isValid) {
        return res.status(401).json({ message: "Invalid username or password" });
      }

      // Login user
      await loginUser(req, user);

      // Return user without password
      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid login data", errors: error.errors });
      }
      console.error("Error logging in user:", error);
      res.status(500).json({ message: "Failed to login" });
    }
  });

  app.post('/api/auth/logout', async (req, res) => {
    try {
      await logoutUser(req);
      res.json({ message: "Logged out successfully" });
    } catch (error) {
      console.error("Error logging out user:", error);
      res.status(500).json({ message: "Failed to logout" });
    }
  });

  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.session as any).userId;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Return user without password
      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Category routes
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Prediction routes
  app.get("/api/predictions", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 20;
      const offset = parseInt(req.query.offset as string) || 0;
      const categoryId = req.query.categoryId as string;

      const predictions = await storage.getPredictions(limit, offset, categoryId);

      // If user is authenticated, include their votes
      if (req.session && (req.session as any).userId) {
        const userId = (req.session as any).userId;
        for (const prediction of predictions) {
          const userVote = await storage.getUserVote(userId, prediction.id);
          if (userVote) {
            (prediction as any).userVote = userVote;
          }
        }
      }

      res.json(predictions);
    } catch (error) {
      console.error("Error fetching predictions:", error);
      res.status(500).json({ message: "Failed to fetch predictions" });
    }
  });

  app.get("/api/predictions/:id", async (req, res) => {
    try {
      const prediction = await storage.getPrediction(req.params.id);
      if (!prediction) {
        return res.status(404).json({ message: "Prediction not found" });
      }

      // If user is authenticated, include their vote
      if (req.session && (req.session as any).userId) {
        const userId = (req.session as any).userId;
        const userVote = await storage.getUserVote(userId, prediction.id);
        if (userVote) {
          (prediction as any).userVote = userVote;
        }
      }

      res.json(prediction);
    } catch (error) {
      console.error("Error fetching prediction:", error);
      res.status(500).json({ message: "Failed to fetch prediction" });
    }
  });

  app.post("/api/predictions", isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.session as any).userId;
      const validatedData = insertPredictionSchema.parse(req.body);

      const prediction = await storage.createPrediction({
        ...validatedData,
        userId,
      });

      res.status(201).json(prediction);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid prediction data", errors: error.errors });
      }
      console.error("Error creating prediction:", error);
      res.status(500).json({ message: "Failed to create prediction" });
    }
  });

  // Vote routes
  app.post("/api/votes", isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.session as any).userId;
      const validatedData = insertVoteSchema.parse(req.body);

      // Check if user already voted on this prediction
      const existingVote = await storage.getUserVote(userId, validatedData.predictionId);
      if (existingVote) {
        return res.status(400).json({ message: "You have already voted on this prediction" });
      }

      // Check if user has enough points
      const user = await storage.getUser(userId);
      if (!user || user.points < validatedData.pointsStaked) {
        return res.status(400).json({ message: "Insufficient points" });
      }

      const vote = await storage.createVote({
        ...validatedData,
        userId,
      });

      // Broadcast vote update via WebSocket
      if (wss) {
        const message = JSON.stringify({
          type: 'vote_update',
          predictionId: validatedData.predictionId,
          vote: vote,
        });

        wss.clients.forEach((client) => {
          if (client.readyState === WebSocket.OPEN) {
            client.send(message);
          }
        });
      }

      res.status(201).json(vote);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid vote data", errors: error.errors });
      }
      console.error("Error creating vote:", error);
      res.status(500).json({ message: "Failed to create vote" });
    }
  });

  // User routes
  app.get("/api/leaderboard", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 50;
      const users = await storage.getLeaderboard(limit);
      res.json(users);
    } catch (error) {
      console.error("Error fetching leaderboard:", error);
      res.status(500).json({ message: "Failed to fetch leaderboard" });
    }
  });

  app.get("/api/users/:id", async (req, res) => {
    try {
      const user = await storage.getUserProfile(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user profile:", error);
      res.status(500).json({ message: "Failed to fetch user profile" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket server for real-time updates
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws) => {
    console.log('WebSocket client connected');

    ws.on('close', () => {
      console.log('WebSocket client disconnected');
    });
  });

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  categories,
  predictions,
  votes,
  type User,
  type UpsertUser,
  type Category,
  type InsertCategory,
  type Prediction,
  type InsertPrediction,
  type Vote,
  type InsertVote,
  type PredictionWithDetails,
  type RegisterData,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, and, isNull } from "drizzle-orm";

export interface IStorage {
  // User operations 
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: RegisterData & { password: string }): Promise<User>;

  // Category operations
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Prediction operations
  getPredictions(limit?: number, offset?: number, categoryId?: string): Promise<PredictionWithDetails[]>;
  getPrediction(id: string): Promise<PredictionWithDetails | undefined>;
  createPrediction(prediction: InsertPrediction & { userId: string }): Promise<Prediction>;
  updatePredictionStats(predictionId: string): Promise<void>;

  // Vote operations
  getUserVote(userId: string, predictionId: string): Promise<Vote | undefined>;
  createVote(vote: InsertVote & { userId: string }): Promise<Vote>;

  // User stats
  updateUserStats(userId: string): Promise<void>;
  getLeaderboard(limit?: number): Promise<User[]>;
  getUserProfile(userId: string): Promise<User | undefined>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(userData: RegisterData & { password: string }): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .returning();
    return user;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories);
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  // Prediction operations
  async getPredictions(limit = 20, offset = 0, categoryId?: string): Promise<PredictionWithDetails[]> {
    const query = db
      .select({
        prediction: predictions,
        user: users,
        category: categories,
      })
      .from(predictions)
      .leftJoin(users, eq(predictions.userId, users.id))
      .leftJoin(categories, eq(predictions.categoryId, categories.id))
      .where(categoryId ? eq(predictions.categoryId, categoryId) : undefined)
      .orderBy(desc(predictions.createdAt))
      .limit(limit)
      .offset(offset);

    const results = await query;

    return results.map((result) => {
      const prediction = result.prediction;
      const totalVotes = prediction.yesVotes + prediction.noVotes;
      const yesPercentage = totalVotes > 0 ? Math.round((prediction.yesVotes / totalVotes) * 100) : 0;
      const noPercentage = 100 - yesPercentage;

      const now = new Date();
      const timeLeft = new Date(prediction.resolutionDate).getTime() - now.getTime();
      const daysLeft = Math.max(0, Math.ceil(timeLeft / (1000 * 60 * 60 * 24)));
      const timeRemaining = daysLeft > 0 ? `${daysLeft}d` : 'Ended';

      return {
        ...prediction,
        user: result.user!,
        category: result.category!,
        yesPercentage,
        noPercentage,
        timeRemaining,
      } as PredictionWithDetails;
    });
  }

  async getPrediction(id: string): Promise<PredictionWithDetails | undefined> {
    const [result] = await db
      .select({
        prediction: predictions,
        user: users,
        category: categories,
      })
      .from(predictions)
      .leftJoin(users, eq(predictions.userId, users.id))
      .leftJoin(categories, eq(predictions.categoryId, categories.id))
      .where(eq(predictions.id, id));

    if (!result) return undefined;

    const prediction = result.prediction;
    const totalVotes = prediction.yesVotes + prediction.noVotes;
    const yesPercentage = totalVotes > 0 ? Math.round((prediction.yesVotes / totalVotes) * 100) : 0;
    const noPercentage = 100 - yesPercentage;

    const now = new Date();
    const timeLeft = new Date(prediction.resolutionDate).getTime() - now.getTime();
    const daysLeft = Math.max(0, Math.ceil(timeLeft / (1000 * 60 * 60 * 24)));
    const timeRemaining = daysLeft > 0 ? `${daysLeft}d` : 'Ended';

    return {
      ...prediction,
      user: result.user!,
      category: result.category!,
      yesPercentage,
      noPercentage,
      timeRemaining,
    } as PredictionWithDetails;
  }

  async createPrediction(prediction: InsertPrediction & { userId: string }): Promise<Prediction> {
    const [newPrediction] = await db.insert(predictions).values(prediction).returning();
    return newPrediction;
  }

  async updatePredictionStats(predictionId: string): Promise<void> {
    const voteStats = await db
      .select({
        totalStakes: sql<number>`count(*)`.mapWith(Number),
        totalPoints: sql<number>`sum(${votes.pointsStaked})`.mapWith(Number),
        yesVotes: sql<number>`sum(case when ${votes.stance} = true then 1 else 0 end)`.mapWith(Number),
        noVotes: sql<number>`sum(case when ${votes.stance} = false then 1 else 0 end)`.mapWith(Number),
        yesPoints: sql<number>`sum(case when ${votes.stance} = true then ${votes.pointsStaked} else 0 end)`.mapWith(Number),
        noPoints: sql<number>`sum(case when ${votes.stance} = false then ${votes.pointsStaked} else 0 end)`.mapWith(Number),
      })
      .from(votes)
      .where(eq(votes.predictionId, predictionId));

    const stats = voteStats[0];
    if (stats) {
      await db
        .update(predictions)
        .set({
          totalStakes: stats.totalStakes || 0,
          totalPoints: stats.totalPoints || 0,
          yesVotes: stats.yesVotes || 0,
          noVotes: stats.noVotes || 0,
          yesPoints: stats.yesPoints || 0,
          noPoints: stats.noPoints || 0,
          updatedAt: new Date(),
        })
        .where(eq(predictions.id, predictionId));
    }
  }

  // Vote operations
  async getUserVote(userId: string, predictionId: string): Promise<Vote | undefined> {
    const [vote] = await db
      .select()
      .from(votes)
      .where(and(eq(votes.userId, userId), eq(votes.predictionId, predictionId)));
    return vote;
  }

  async createVote(vote: InsertVote & { userId: string }): Promise<Vote> {
    const [newVote] = await db.insert(votes).values(vote).returning();

    // Update prediction stats
    await this.updatePredictionStats(vote.predictionId);

    // Update user points
    await db
      .update(users)
      .set({
        points: sql`${users.points} - ${vote.pointsStaked}`,
        updatedAt: new Date(),
      })
      .where(eq(users.id, vote.userId));

    return newVote;
  }

  // User stats
  async updateUserStats(userId: string): Promise<void> {
    const userStats = await db
      .select({
        totalPredictions: sql<number>`count(distinct ${predictions.id})`.mapWith(Number),
        totalVotes: sql<number>`count(distinct ${votes.id})`.mapWith(Number),
      })
      .from(users)
      .leftJoin(predictions, eq(predictions.userId, userId))
      .leftJoin(votes, eq(votes.userId, userId))
      .where(eq(users.id, userId));

    const stats = userStats[0];
    if (stats) {
      await db
        .update(users)
        .set({
          totalPredictions: stats.totalPredictions || 0,
          updatedAt: new Date(),
        })
        .where(eq(users.id, userId));
    }
  }

  async getLeaderboard(limit = 50): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .orderBy(desc(users.points), desc(users.accuracyScore))
      .limit(limit);
  }

  async getUserProfile(userId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, userId));
    return user;
  }
}

export const storage = new DatabaseStorage();

